import { useEffect, useState } from 'react';
import { auth } from './firebase';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import MainContent from './pages/MainContent';
import AdminDashboard from './pages/AdminDashboard';

function App() {
  const [user, setUser] = useState(null); // Stores the authenticated user
  const [loading, setLoading] = useState(true); // Tracks loading state

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((currentUser) => {
      setUser(currentUser);
      setLoading(false); // Stop loading once the auth state is known
    });
    return () => unsubscribe();
  }, []);

  if (loading) {
    return <div className="text-center mt-20">Loading...</div>; // Show loading spinner or text
  }

  return (
    <Router>
      <Routes>
        <Route path="/login" element={!user ? <Login /> : <Navigate to="/" />} />
        <Route path="/register" element={!user ? <Register /> : <Navigate to="/" />} />
        <Route path="/admin" element={user?.role === 'admin' ? <AdminDashboard /> : <Navigate to="/login" />} />
        <Route path="/" element={user ? <MainContent /> : <Navigate to="/login" />} />
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
