import React, { useState, useEffect, useRef } from 'react';
import './index.css';
import { auth, db } from './firebase';
import Auth from './Auth';
import { collection, addDoc, getDocs, deleteDoc, doc, updateDoc } from 'firebase/firestore';

function App() {
  const [itinerary, setItinerary] = useState({
    location: '',
    date: '',
    activities: '',
    budget: '',
    actualBudget: '',
  });
  const [itineraries, setItineraries] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [user, setUser] = useState(null);

  // Reference to scroll to the form
  const formRef = useRef(null);

  // Listen for authentication state changes
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(currentUser => {
      setUser(currentUser);
    });
    return () => unsubscribe();
  }, []);

  // Fetch itineraries from Firestore
  const fetchItineraries = async () => {
    try {
      const itinerariesCollection = collection(db, 'itineraries');
      const itinerariesSnapshot = await getDocs(itinerariesCollection);
      const itinerariesList = itinerariesSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setItineraries(itinerariesList);
    } catch (error) {
      console.error('Error fetching itineraries:', error);
    }
  };

  // Fetch itineraries on mount
  useEffect(() => {
    fetchItineraries();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editIndex !== null) {
        // Update existing itinerary
        const itineraryDoc = doc(db, 'itineraries', itineraries[editIndex].id);
        await updateDoc(itineraryDoc, itinerary);
        const updatedItineraries = itineraries.map((item, index) =>
          index === editIndex ? { ...item, ...itinerary } : item
        );
        setItineraries(updatedItineraries);
      } else {
        // Add new itinerary
        const docRef = await addDoc(collection(db, 'itineraries'), itinerary);
        setItineraries([...itineraries, { ...itinerary, id: docRef.id }]);
      }
      // Reset form
      setItinerary({ location: '', date: '', activities: '', budget: '', actualBudget: '' });
      setEditIndex(null);
    } catch (error) {
      console.error('Error saving itinerary:', error);
    }
  };

  const handleEdit = (index) => {
    setItinerary(itineraries[index]);
    setEditIndex(index);

    // Scroll to the form when the edit button is clicked
    if (formRef.current) {
      formRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleDelete = async (index) => {
    const itineraryDoc = doc(db, 'itineraries', itineraries[index].id);
    await deleteDoc(itineraryDoc);
    const updatedItineraries = itineraries.filter((_, i) => i !== index);
    setItineraries(updatedItineraries);
  };

  return (
    <div className="App text-center min-h-screen py-8 px-4">
  <header className="mb-12">
    <h1>üåü Crowdsourced Itinerary Planner</h1>
    <p>Your ultimate travel companion.</p>
  </header>
  <Auth user={user} />

  {user && (
    <div className="max-w-3xl mx-auto">
      <form
        onSubmit={handleSubmit}
        ref={formRef}
        className="bg-glass shadow-lg space-y-4"
      >
        <h2>{editIndex !== null ? 'Edit Itinerary' : 'Create New Itinerary'}</h2>
        <input
          type="text"
          placeholder="Location"
          value={itinerary.location}
          onChange={(e) => setItinerary({ ...itinerary, location: e.target.value })}
          required
          className="input-glass"
        />
        <input
          type="date"
          value={itinerary.date}
          onChange={(e) => setItinerary({ ...itinerary, date: e.target.value })}
          required
          className="input-glass"
        />
        <textarea
          placeholder="Activities"
          value={itinerary.activities}
          onChange={(e) => setItinerary({ ...itinerary, activities: e.target.value })}
          required
          className="input-glass"
        />
        <input
          type="number"
          placeholder="Estimated Budget"
          value={itinerary.budget}
          onChange={(e) => setItinerary({ ...itinerary, budget: e.target.value })}
          required
          className="input-glass"
        />
        <input
          type="number"
          placeholder="Actual Budget"
          value={itinerary.actualBudget}
          onChange={(e) => setItinerary({ ...itinerary, actualBudget: e.target.value })}
          required
          className="input-glass"
        />
        <button type="submit" className="button-modern w-full">
          {editIndex !== null ? 'Update Itinerary' : 'Save Itinerary'}
        </button>
      </form>

      <h2 className="mt-12">Your Itineraries</h2>
      <div className="space-y-4">
        {itineraries.length > 0 ? (
          itineraries.map((item, index) => (
            <div className="card" key={item.id}>
              <h3>üìç {item.location}</h3>
              <p>Date: {item.date}</p>
              <p>Activities: {item.activities}</p>
              <p>Budget: {item.budget || 'N/A'}</p>
              <p>Actual Budget: {item.actualBudget || 'N/A'}</p>
              <div className="flex justify-end space-x-4 mt-4">
                <button onClick={() => handleEdit(index)} className="px-6 py-3 font-bold text-white bg-gradient-to-r from-cyan-400 to-blue-600 rounded-full shadow-lg transform transition hover:scale-105 hover:shadow-xl active:scale-95">
                  ‚úèÔ∏è Edit
                </button>
                <button
                  onClick={() => handleDelete(index)}
                 className="px-6 py-3 font-bold text-white bg-gradient-to-r from-red-400 to-red-600 rounded-full shadow-lg transform transition hover:scale-105 hover:shadow-xl active:scale-95"
                >
                  üóëÔ∏è Delete
                </button>
              </div>
            </div>
          ))
        ) : (
          <p className="text-gray-400">No itineraries saved yet.</p>
        )}
      </div>
    </div>
  )}
</div>


  );
}

export default App;
