import React, { useState } from 'react';
import Login from './pages/Login.js';   // Ensure this path is correct based on your file structure
import Register from './pages/Register.js'; // Ensure this path is correct based on your file structure

function Auth({ user, setUser }) {
    return (
      <div>
        {user ? (
          <p>Welcome, {user.email}!</p>
        ) : (
          <>
            <h2>Login</h2>
            <Login setUser={setUser} />
            <h2>Register</h2>
            <Register setUser={setUser} />
          </>
        )}
      </div>
    );
  }

  const handleLogout = async () => {
    try {
        await auth.signOut();
        setUser(null); // Clear the user state
    } catch (error) {
        console.error("Error logging out:", error);
    }
};


export default Auth;
