// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Auth from './Auth';
import MainContent from './MainContent';
import Login from '/pages/login';
import Register from '/pages/register';
import AdminPage from './pages/admin';
import './index.css';

function App() {
  return (
    <Router>
      <div className="App text-center min-h-screen bg-gray-100 py-8 px-4">
        <header className="mb-10">
          <h1 className="text-5xl font-bold text-blue-800">Crowdsourced Itinerary Planner</h1>
          <p className="text-gray-600 mt-2 text-lg">Plan, organize, and save your travel itineraries effortlessly.</p>
        </header>

        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/admin" element={<AdminPage />} />
          <Route path="/" element={<MainContent />} /> {/* Main content here */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
