import React, { useState, useEffect } from 'react';
import { auth, db } from './firebase'; 
import Auth from './Auth'; 
import { collection, addDoc, getDocs, deleteDoc, doc, updateDoc } from 'firebase/firestore'; 

function App() {
  const [itinerary, setItinerary] = useState({ location: '', date: '', activities: '' });
  const [itineraries, setItineraries] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(currentUser => {
      setUser(currentUser);
    });
    return () => unsubscribe();
  }, []);

  // Fetch itineraries from Firestore
  const fetchItineraries = async () => {
    const itinerariesCollection = collection(db, 'itineraries');
    const itinerariesSnapshot = await getDocs(itinerariesCollection);
    const itinerariesList = itinerariesSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    setItineraries(itinerariesList);
  };

  // Fetch itineraries on mount
  useEffect(() => {
    fetchItineraries();
  }, []);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (editIndex !== null) {
        // Update itinerary
        const itineraryDoc = doc(db, 'itineraries', itineraries[editIndex].id);
        await updateDoc(itineraryDoc, itinerary);
        const updatedItineraries = itineraries.map((item, index) => (index === editIndex ? itinerary : item));
        setItineraries(updatedItineraries);
      } else {
        // Save new itinerary
        await addDoc(collection(db, 'itineraries'), itinerary);
        setItineraries([...itineraries, itinerary]);
      }

      // Reset form
      setItinerary({ location: '', date: '', activities: '' });
      setEditIndex(null); // Reset edit state
    } catch (error) {
      console.error('Error saving itinerary:', error);
    }
  };

  // Handle edit action
  const handleEdit = (index) => {
    setItinerary(itineraries[index]);
    setEditIndex(index);
  };

  // Handle delete action
  const handleDelete = async (index) => {
    const itineraryDoc = doc(db, 'itineraries', itineraries[index].id);
    await deleteDoc(itineraryDoc);
    const updatedItineraries = itineraries.filter((_, i) => i !== index);
    setItineraries(updatedItineraries);
  };

  return (
    <div className="App">
      <h1>Crowdsourced Itinerary Planner</h1>
      <Auth user={user} setUser={setUser} /> {/* Render Auth component, passing the user state */}

      {user && ( // Only show form if user is logged in
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Location"
            value={itinerary.location}
            onChange={(e) => setItinerary({ ...itinerary, location: e.target.value })}
            required
          />
          <input
            type="date"
            value={itinerary.date}
            onChange={(e) => setItinerary({ ...itinerary, date: e.target.value })}
            required
          />
          <textarea
            placeholder="Activities"
            value={itinerary.activities}
            onChange={(e) => setItinerary({ ...itinerary, activities: e.target.value })}
            required
          />
          <button type="submit">{editIndex !== null ? 'Update' : 'Save'} Itinerary</button>
        </form>
      )}

      <h2>Saved Itineraries</h2>
      <ul>
        {itineraries.map((item, index) => (
          <li key={item.id}>
            <strong>{item.location}</strong> - {item.date}
            <p>{item.activities}</p>
            <button onClick={() => handleEdit(index)}>Edit</button>
            <button onClick={() => handleDelete(index)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
