import React, { useState, useEffect } from 'react';
import './index.css'; // Ensure this is at the top of your file
import { auth, db } from './firebase';
import Auth from './Auth';
import { collection, addDoc, getDocs, deleteDoc, doc, updateDoc } from 'firebase/firestore';

function App() {
  const [itinerary, setItinerary] = useState({ location: '', date: '', activities: '' });
  const [itineraries, setItineraries] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [user, setUser] = useState(null);

  // Listen for authentication state changes
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(currentUser => {
      setUser(currentUser);
    });
    return () => unsubscribe();
  }, []);

  // Fetch itineraries from Firestore
  const fetchItineraries = async () => {
    try {
      const itinerariesCollection = collection(db, 'itineraries');
      const itinerariesSnapshot = await getDocs(itinerariesCollection);
      const itinerariesList = itinerariesSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setItineraries(itinerariesList);
    } catch (error) {
      console.error('Error fetching itineraries:', error);
    }
  };

  // Fetch itineraries on mount
  useEffect(() => {
    fetchItineraries();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editIndex !== null) {
        // Update itinerary
        const itineraryDoc = doc(db, 'itineraries', itineraries[editIndex].id);
        await updateDoc(itineraryDoc, itinerary);
        const updatedItineraries = itineraries.map((item, index) => 
          index === editIndex ? itinerary : item
        );
        setItineraries(updatedItineraries);
      } else {
        // Save new itinerary
        const docRef = await addDoc(collection(db, 'itineraries'), itinerary);
        setItineraries([...itineraries, { ...itinerary, id: docRef.id }]);
      }
  
      // Reset form
      setItinerary({ location: '', date: '', activities: '' });
      setEditIndex(null); // Reset edit state
    } catch (error) {
      console.error('Error saving itinerary:', error);
    }
  };

  // Handle edit action
  const handleEdit = (index) => {
    setItinerary(itineraries[index]);
    setEditIndex(index);
  };

  // Handle delete action
  const handleDelete = async (index) => {
    const itineraryDoc = doc(db, 'itineraries', itineraries[index].id);
    await deleteDoc(itineraryDoc);
    const updatedItineraries = itineraries.filter((_, i) => i !== index);
    setItineraries(updatedItineraries);
  };

  return (
    <div className="App text-center mt-12 px-4">
      <h1 className="text-4xl font-bold text-blue-700 mb-6">Crowdsourced Itinerary Planner</h1>
      <Auth user={user} />

      {user && (
        <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md mt-4 space-y-4">
          <input
            type="text"
            placeholder="Location"
            value={itinerary.location}
            onChange={(e) => setItinerary({ ...itinerary, location: e.target.value })}
            required
            className="border border-gray-300 p-3 rounded w-full text-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-200"
          />
          <input
            type="date"
            value={itinerary.date}
            onChange={(e) => setItinerary({ ...itinerary, date: e.target.value })}
            required
            className="border border-gray-300 p-3 rounded w-full text-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-200"
          />
          <textarea
            placeholder="Activities"
            value={itinerary.activities}
            onChange={(e) => setItinerary({ ...itinerary, activities: e.target.value })}
            required
            className="border border-gray-300 p-3 rounded w-full text-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-200"
          />
          <button type="submit" className="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 transition duration-200">
            {editIndex !== null ? 'Update' : 'Save'} Itinerary
          </button>
        </form>
      )}

      <h2 className="text-2xl mt-6 mb-2 font-semibold">Saved Itineraries</h2>
      <ul className="list-none p-0">
        {itineraries.length > 0 ? (
          itineraries.map((item, index) => (
            <li key={item.id} className="bg-white p-4 mb-4 rounded-lg shadow hover:shadow-lg transition duration-200">
              <strong className="text-xl">{item.location}</strong> - <span className="text-gray-600">{item.date}</span>
              <p className="mt-2 text-gray-700">{item.activities}</p>
              <div className="mt-4">
                <button onClick={() => handleEdit(index)} className="text-blue-600 mr-2 hover:underline transition duration-200">
                  Edit
                </button>
                <button onClick={() => handleDelete(index)} className="text-red-600 hover:underline transition duration-200">
                  Delete
                </button>
              </div>
            </li>
          ))
        ) : (
          <p className="text-gray-500">No itineraries saved yet.</p>
        )}
      </ul>
    </div>
  );
}

export default App;
