import React from 'react';
import Login from './pages/Login';   // Ensure this path is correct based on your file structure
import Register from './pages/Register'; // Ensure this path is correct based on your file structure
import { auth } from './firebase'; // Make sure to import Firebase auth

function Auth({ user, setUser }) {
  
  const handleLogout = async () => {
    try {
      await auth.signOut();
      setUser(null); // Clear the user state
    } catch (error) {
      console.error("Error logging out:", error);
    }
  };

  return (
    <div>
      {user ? (
        <div>
          <p>Welcome, {user.email}!</p>
          <button onClick={handleLogout}>Logout</button> {/* Add Logout button */}
        </div>
      ) : (
        <>
          <h2>Login</h2>
          <Login setUser={setUser} />
          <h2>Register</h2>
          <Register setUser={setUser} />
        </>
      )}
    </div>
  );
}

export default Auth;
