import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import MainContent from './MainContent'; // Content page for regular users
import AdminDashboard from './AdminDashboard'; // Dashboard for admin
import { auth, db } from './firebase';
import { doc, getDoc } from 'firebase/firestore';

function App() {
  const [user, setUser] = useState(null); // Track authenticated user
  const [role, setRole] = useState(null); // Track user role
  const [loading, setLoading] = useState(true); // Loading state

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(async (currentUser) => {
      if (currentUser) {
        setUser(currentUser);
        const userDoc = await getDoc(doc(db, 'users', currentUser.uid));
        if (userDoc.exists()) {
          setRole(userDoc.data().role);
        }
      } else {
        setUser(null);
        setRole(null);
      }
      setLoading(false); // Loading completed
    });
    return () => unsubscribe();
  }, []);

  if (loading) {
    return <div>Loading...</div>; // Show loading spinner while auth state is checked
  }

  return (
    <Router>
      <Routes>
        {/* Login and Register */}
        <Route path="/login" element={!user ? <Login /> : <Navigate to="/" />} />
        <Route path="/register" element={!user ? <Register /> : <Navigate to="/" />} />

        {/* Admin Dashboard */}
        <Route path="/admin" element={role === 'admin' ? <AdminDashboard /> : <Navigate to="/login" />} />

        {/* Regular User */}
        <Route path="/" element={role === 'user' ? <MainContent /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
