import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import './index.css';
import { auth, db } from './firebase';
import Auth from './Auth';
import Profile from './Profile'; // Import Profile component here
import Home from './Home'; // Assuming you moved the main planner code to a separate Home component for cleaner routing

function App() {
  return (
    <BrowserRouter>
      <div className="App">
        {/* Navigation Bar */}
        <nav className="bg-blue-600 text-white py-3 px-5 flex justify-between">
          <a href="/" className="text-lg font-semibold">Home</a>
          <a href="/profile" className="text-lg font-semibold">Profile</a>
        </nav>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<Home />} />        {/* Main Planner Page */}
          <Route path="/profile" element={<Profile />} /> {/* Profile Page */}
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
