import React, { useState, useEffect } from 'react';
import { saveItinerary, getItineraries } from './itineraryUtils';
import { auth, db } from './firebase'; // Import Firebase services

function App() {
  const [itinerary, setItinerary] = useState({ location: '', date: '', activities: '' });
  const [itineraries, setItineraries] = useState([]);
  const [editIndex, setEditIndex] = useState(null);

  useEffect(() => {
    setItineraries(getItineraries());
  }, []);

  function handleSubmit(e) {
    e.preventDefault();
    if (editIndex !== null) {
      const updatedItineraries = itineraries;
      updatedItineraries[editIndex] = itinerary;
      localStorage.setItem('itineraries', JSON.stringify(updatedItineraries));
      setEditIndex(null); // Reset edit state
    } else {
      saveItinerary(itinerary);
    }
    setItinerary({ location: '', date: '', activities: '' });
    setItineraries(getItineraries());
  }

  function handleEdit(index) {
    setItinerary(itineraries[index]);
    setEditIndex(index);
  }
  function handleDelete(index) {
    const updatedItineraries = itineraries.filter((_, i) => i !== index);
    localStorage.setItem('itineraries', JSON.stringify(updatedItineraries));
    setItineraries(updatedItineraries);
  }
  
  useEffect(() => {
    // Example: Check if the user is authenticated
    auth.onAuthStateChanged(user => {
      if (user) {
        console.log('User is logged in:', user);
      } else {
        console.log('No user is logged in.');
      }
    });
  }, []);

  
  return (
    <div className="App">
      <h1>Crowdsourced Itinerary Planner</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Location"
          value={itinerary.location}
          onChange={(e) => setItinerary({ ...itinerary, location: e.target.value })}
        />
        <input
          type="date"
          value={itinerary.date}
          onChange={(e) => setItinerary({ ...itinerary, date: e.target.value })}
        />
        <textarea
          placeholder="Activities"
          value={itinerary.activities}
          onChange={(e) => setItinerary({ ...itinerary, activities: e.target.value })}
        />
        <button type="submit">{editIndex !== null ? 'Update' : 'Save'} Itinerary</button>
      </form>

      <h2>Saved Itineraries</h2>
      <ul>
        {itineraries.map((itinerary, index) => (
          <li key={index}>
            <strong>{itinerary.location}</strong> - {itinerary.date}
            <p>{itinerary.activities}</p>
            <button onClick={() => handleEdit(index)}>Edit</button>
            <button onClick={() => handleDelete(index)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}


export default App;
