import React, { useState, useEffect, useRef } from 'react';
import './index.css';
import { auth, db } from './firebase';
import Auth from './Auth';
import { collection, addDoc, getDocs, deleteDoc, doc, updateDoc } from 'firebase/firestore';

function App() {
  const [itinerary, setItinerary] = useState({
    location: '',
    date: '',
    activities: '',
    budget: '',
    actualBudget: '',
  });
  const [itineraries, setItineraries] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [user, setUser] = useState(null);

  // Reference to scroll to the form
  const formRef = useRef(null);

  // Listen for authentication state changes
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(currentUser => {
      setUser(currentUser);
    });
    return () => unsubscribe();
  }, []);

  // Fetch itineraries from Firestore
  const fetchItineraries = async () => {
    try {
      const itinerariesCollection = collection(db, 'itineraries');
      const itinerariesSnapshot = await getDocs(itinerariesCollection);
      const itinerariesList = itinerariesSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data(),
      }));
      setItineraries(itinerariesList);
    } catch (error) {
      console.error('Error fetching itineraries:', error);
    }
  };

  // Fetch itineraries on mount
  useEffect(() => {
    fetchItineraries();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editIndex !== null) {
        const itineraryDoc = doc(db, 'itineraries', itineraries[editIndex].id);
        await updateDoc(itineraryDoc, itinerary);
        const updatedItineraries = itineraries.map((item, index) =>
          index === editIndex ? itinerary : item
        );
        setItineraries(updatedItineraries);
      } else {
        const docRef = await addDoc(collection(db, 'itineraries'), itinerary);
        setItineraries([...itineraries, { ...itinerary, id: docRef.id }]);
      }
      setItinerary({ location: '', date: '', activities: '', budget: '', actualBudget: '' });
      setEditIndex(null);
    } catch (error) {
      console.error('Error saving itinerary:', error);
    }
  };

  const handleEdit = (index) => {
    setItinerary(itineraries[index]);
    setEditIndex(index);

    // Scroll to the form when the edit button is clicked
    if (formRef.current) {
      formRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleDelete = async (index) => {
    const itineraryDoc = doc(db, 'itineraries', itineraries[index].id);
    await deleteDoc(itineraryDoc);
    const updatedItineraries = itineraries.filter((_, i) => i !== index);
    setItineraries(updatedItineraries);
  };

  return (
    <div className="App text-center min-h-screen bg-gray-100 py-8 px-4">
      <header className="mb-10">
        <h1 className="text-5xl font-bold text-blue-800">Crowdsourced Itinerary Planner</h1>
        <p className="text-gray-600 mt-2 text-lg">Plan, organize, and save your travel itineraries effortlessly.</p>
      </header>
      <Auth user={user} />

      {user && (
        <div className="max-w-2xl mx-auto">
          <form onSubmit={handleSubmit} ref={formRef} className="bg-white p-6 rounded-lg shadow-md space-y-4">
            <h2 className="text-2xl font-semibold text-gray-700">
              {editIndex !== null ? 'Edit Itinerary' : 'Add New Itinerary'}
            </h2>
            <input
              type="text"
              placeholder="Location"
              value={itinerary.location}
              onChange={(e) => setItinerary({ ...itinerary, location: e.target.value })}
              required
              className="w-full border border-gray-300 p-3 rounded-lg focus:ring-2 focus:ring-blue-500"
            />
            <input
              type="date"
              value={itinerary.date}
              onChange={(e) => setItinerary({ ...itinerary, date: e.target.value })}
              required
              className="w-full border border-gray-300 p-3 rounded-lg focus:ring-2 focus:ring-blue-500"
            />
            <input
             type="number"
             placeholder="Estimated Budget"
             value={itinerary.budget}
             onChange={(e) => setItinerary({ ...itinerary, budget: e.target.value })}
             required
             className="w-full border border-gray-300 p-3 rounded-lg focus:ring-2 focus:ring-blue-500"
            />
            <input
             type="number"
             placeholder="Actual Budget"
             value={itinerary.actualBudget}
             onChange={(e) => setItinerary({ ...itinerary, actualBudget: e.target.value })}
             required
             className="w-full border border-gray-300 p-3 rounded-lg focus:ring-2 focus:ring-blue-500"
            />
            <textarea
              placeholder="Activities"
              value={itinerary.activities}
              onChange={(e) => setItinerary({ ...itinerary, activities: e.target.value })}
              required
              className="w-full border border-gray-300 p-3 rounded-lg focus:ring-2 focus:ring-blue-500"
            />
            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition"
            >
              {editIndex !== null ? 'Update Itinerary' : 'Save Itinerary'}
            </button>
          </form>

          <h2 className="text-3xl font-bold text-gray-700 mt-10 mb-4">Saved Itineraries</h2>
          <ul className="space-y-4">
            {itineraries.length > 0 ? (
              itineraries.map((item, index) => (
                <li
                  key={item.id}
                  className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition"
                >
                  <h3 className="text-xl font-semibold text-blue-700">{item.location}</h3>
                  <p className="text-gray-500">{item.date}</p>
                  <p className="text-gray-600 mt-2">{item.activities}</p>
                  <div className="flex justify-end space-x-4 mt-4">
                    <button 
                      onClick={() => handleEdit(index)} 
                      className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded-lg shadow-md transition-all duration-200 transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-blue-300"
                    >
                      Edit
                    </button>
                    <button 
                      onClick={() => handleDelete(index)} 
                      className="bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded-lg shadow-md transition-all duration-200 transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-red-300"
                    >
                      Delete
                    </button>
                  </div>
                </li>
              ))
            ) : (
              <p className="text-gray-500">No itineraries saved yet.</p>
            )}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
